Darmowa dostawa od 150 PLN<br>
<a href="/zwroty" target="_blank">30 dni na darmowy zwrot</a>
<script>document.querySelector(`[data-qa-variant_name="Standardowa (D)"]`)&&document.querySelector(`[data-qa-variant_name="Standardowa (D)"`).click();


function addReturnsInfo() {
    setTimeout(() => {
        const targetImg = document.querySelector('img[data-nimg]');
        if (targetImg) {
            const parent = targetImg.closest('span');
            if (parent && !parent.querySelector('.returns-info')) {
                parent.style.position = 'relative';

                const link = Object.assign(document.createElement('a'), {
                    href: '/zwroty',
                    className: 'returns-info',
                    style: 'position: absolute; top: 10px; z-index: 10;',
                    target: '_blank'
                });

                const icoInfo = Object.assign(document.createElement('img'), {
                    src: 'https://nbsklep.pl/picture/files/upload/icon/zwrot-30-dni.png',
                    alt: '30 dni na darmowy zwrot',
                    style: 'height: 90px; pointer-events: none;'
                });

                link.appendChild(icoInfo);
                parent.insertBefore(link, targetImg);

                const updatePosition = () => {
                    link.style.cssText += window.innerWidth <= 768
                        ? 'right: 10px; left: auto;'
                        : 'left: 10px; right: auto;';
                };

                updatePosition();
                window.addEventListener('resize', updatePosition);
            }
        }
    }, 50);
}


if (!document.querySelector(".returns-info")) {
	addReturnsInfo();

	var targetSelector = "h1[data-qa-product_name]";

	let targetNode = document.querySelector(targetSelector);

	// Sprawdź, czy element istnieje
	if (targetNode) {
		// Opcje dla obserwatora
		const config = {
			attributes: true,
			childList: true,
			characterData: true,
		};

		const observer = new MutationObserver(function (mutations) {
			mutations.forEach(function (mutation) {
				// Wykryto zmianę, wywołaj funkcję
				addReturnsInfo();
			});
		});

		observer.observe(targetNode, config);
	} else {
		console.warn(`Element ${targetSelector} nie znaleziony.`);
	}
}
</script>
